namespace BclExtensionMethodTests.Email
{
	using System.Net.Mail;
	using BclExtensionMethods.Email;
	using BclExtensionMethods.Streams;
	using NUnit.Framework;
	using log4net.Config;

	[TestFixture]
	public class ManualEmailTests
	{
		[Test]
		[Ignore]
		public void SendDebugEmail()
		{
			BasicConfigurator.Configure();
			EmailConfiguration.Configuration.SetDebugMode();
			var message = GetTestMessage();

			Email.SendEmail(message);
		}

		[Test]
		[Ignore]
		public void SendRealEmail()
		{
			EmailConfiguration.Configuration.SetSendMode();
			var message = GetTestMessage();

			Email.SendEmail(message);
		}

		private static MailMessage GetTestMessage()
		{
			var message = new MailMessage
			              	{
			              		Subject = "debug test",
			              		Body = "body test",
			              	};
			message.To.Add("wes.mcclure@gmail.com");
			message.CC.Add("wes.mcclure+cc@gmail.com");
			message.Bcc.Add("wes.mcclure+bcc@gmail.com");
			message.From = new MailAddress("wes.mcclure+from@gmail.com");

			#region Image Attachment

			var trollImageStream = ""
				.HexStringToStream();

			#endregion

			message.Attachments.Add(new Attachment(trollImageStream, "troll.jpg", "image/jpeg"));
			return message;
		}
	}
}